$(document).ready(function(){$("input[name^='product-']:checkbox").change(function(){if($(this).is(":checked")){var t=$(this).closest(".act-product").nextUntil(".act-product").filter(":first").find("input:radio");t.attr("checked","checked"),t.button("refresh")}else $(this).closest(".act-product").nextUntil(".act-product").each(function(){$(this).find("input:radio").removeProp("checked")}),$("input:radio").button("refresh")}),$("input[name^='price-']:radio").change(function(){if(!$(this).is(":checked"))return!1;var t=$(this).closest(".act-price").prevUntil(".act-product").prev().find("input:checkbox");t.is(":checked")||t.attr("checked","checked")})});